import React, { useState } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";

const ChessGame = () => {
  const [game, setGame] = useState(new Chess()); // Chess.js game instance

  // Handle piece drops
  const onDrop = (sourceSquare, targetSquare) => {
    const newGame = { ...game };
    try {
      const move = newGame.move({
        from: sourceSquare,
        to: targetSquare,
        promotion: "q", // Promote to queen by default
      });

      if (move) {
        setGame(newGame); // Update game state
        return true;
      }
    } catch (error) {
      console.log("Invalid move:", error);
      return false;
    }
    return false;
  };

  // Undo move
  const handleUndo = () => {
    const newGame = { ...game };
    newGame.undo();
    setGame(newGame);
  };

  return (
    <div className="chess-container" style={styles.container}>
      <h1 style={styles.title}>React Chess Game</h1>
      <Chessboard
        position={game.fen()}
        onPieceDrop={onDrop}
        boardWidth={250} /* 50% of original size */
      />
      <button onClick={handleUndo} style={styles.button}>
        Undo Move
      </button>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "100vh",
    backgroundColor: "#f0f0f0",
  },
  title: {
    marginBottom: "20px",
    fontSize: "24px",
    color: "#333",
  },
  button: {
    marginTop: "10px",
    padding: "8px 16px",
    fontSize: "14px",
    backgroundColor: "#ff6347",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
};

export default ChessGame;
