import React, { useState } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";

const ChessGame = () => {
  const [game, setGame] = useState(new Chess()); // Initialize Chess.js instance
  const [fen, setFen] = useState(game.fen()); // Board position in FEN format

  // Function to handle moves
  const onDrop = (sourceSquare, targetSquare) => {
    const newGame = { ...game };

    try {
      const move = newGame.move({
        from: sourceSquare,
        to: targetSquare,
        promotion: "q", // Always promote to a queen for simplicity
      });

      if (move) {
        setGame(newGame);
        setFen(newGame.fen()); // Update FEN
      }
    } catch (error) {
      console.log("Invalid move:", error);
      return false; // Invalid move
    }
    return true;
  };

  // Function to undo the last move
  const undoMove = () => {
    const newGame = { ...game };
    newGame.undo();
    setGame(newGame);
    setFen(newGame.fen());
  };

  return (
    <div className="chess-game">
      <h1>React Chess Game</h1>
      <Chessboard
        position={fen}
        onPieceDrop={onDrop} // Handle piece movement
      />
      <button onClick={undoMove} className="undo-button">
        Undo Move
      </button>
    </div>
  );
};

export default ChessGame;
