import React, { useState } from "react";
import { Chess } from "chess.js"; // Import Chess.js for game logic
import { Chessboard } from "react-chessboard"; // Import Chessboard for rendering the board

const ChessGame = () => {
  const [game, setGame] = useState(new Chess()); // Create a Chess.js game instance

  // Handle piece moves
  const onDrop = (sourceSquare, targetSquare) => {
    const newGame = { ...game }; // Clone the game state

    try {
      const move = newGame.move({
        from: sourceSquare,
        to: targetSquare,
        promotion: "q", // Automatically promote to queen
      });

      if (move) {
        setGame(newGame); // Update game state on a valid move
        return true;
      }
    } catch (error) {
      console.log("Invalid move:", error);
      return false; // Reject invalid move
    }
    return false;
  };

  // Undo the last move
  const handleUndo = () => {
    const newGame = { ...game };
    newGame.undo();
    setGame(newGame);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>React Chess Game</h1>
      <Chessboard
        position={game.fen()} // Pass current FEN position to the board
        onPieceDrop={onDrop} // Handle piece movements
        boardWidth={500} // Set board size
      />
      <button
        onClick={handleUndo}
        style={{
          marginTop: "20px",
          padding: "10px 20px",
          backgroundColor: "#ff6347",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Undo Move
      </button>
    </div>
  );
};

export default ChessGame;
